import React, { useState } from "react";
import axios from "axios";

const AddRelationship = () => {
    const [node1Label, setNode1Label] = useState("");
    const [node1Props, setNode1Props] = useState("{}");
    const [node2Label, setNode2Label] = useState("");
    const [node2Props, setNode2Props] = useState("{}");
    const [relationshipType, setRelationshipType] = useState("");
    const [response, setResponse] = useState("");

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const parsedNode1Props = JSON.parse(node1Props);
            const parsedNode2Props = JSON.parse(node2Props);
            const res = await axios.post("http://127.0.0.1:8000/add_relationship", {
                node1_label: node1Label,
                node1_properties: parsedNode1Props,
                node2_label: node2Label,
                node2_properties: parsedNode2Props,
                relationship_type: relationshipType,
            });
            setResponse(res.data);
        } catch (error) {
            console.error(error);
            setResponse(error.message);
        }
    };

    return (
        <div style={{ textAlign: "center", marginTop: "50px" }}>
            <h2>Add Relationship</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Node 1 Label:</label>
                    <input
                        type="text"
                        value={node1Label}
                        onChange={(e) => setNode1Label(e.target.value)}
                        style={{ margin: "10px" }}
                    />
                </div>
                <div>
                    <label>Node 1 Properties (JSON):</label>
                    <textarea
                        value={node1Props}
                        onChange={(e) => setNode1Props(e.target.value)}
                        style={{ margin: "10px", width: "300px", height: "100px" }}
                    />
                </div>
                <div>
                    <label>Node 2 Label:</label>
                    <input
                        type="text"
                        value={node2Label}
                        onChange={(e) => setNode2Label(e.target.value)}
                        style={{ margin: "10px" }}
                    />
                </div>
                <div>
                    <label>Node 2 Properties (JSON):</label>
                    <textarea
                        value={node2Props}
                        onChange={(e) => setNode2Props(e.target.value)}
                        style={{ margin: "10px", width: "300px", height: "100px" }}
                    />
                </div>
                <div>
                    <label>Relationship Type:</label>
                    <input
                        type="text"
                        value={relationshipType}
                        onChange={(e) => setRelationshipType(e.target.value)}
                        style={{ margin: "10px" }}
                    />
                </div>
                <button type="submit">Add Relationship</button>
            </form>
            {response && (
                <div style={{ marginTop: "20px" }}>
                    <h3>Response:</h3>
                    <pre>{JSON.stringify(response, null, 2)}</pre>
                </div>
            )}
        </div>
    );
};

export default AddRelationship;
